INSERT INTO `t_project` VALUES (116, '[靖安县]靖安县高湖学校运动场建设工程', '房建市政', '', '<div class=\"article-info\">\n            				<h1>[靖安县]靖安县高湖学校运动场建设工程<font color=\"red\"></font></h1>\n            				<p class=\"infotime\">\n            					[2019-06-15]\n            				</p>\n            				<div class=\"con\" style=\"margin-top: 31px;\">\n\n\n    <meta charset=\"utf-8\"/>\n    <meta content=\"IE=edge\" http-equiv=\"X-UA-Compatible\"/>\n    <style>\n    tr{\n    height:30px;\n    }\n    </style>\n\n\n    <div align=\"center\">\n        <div style=\"text-align: right;\">\n            <span style=\"font-family: 黑体; font-size: 14pt;\">招标投标格式文本二</span>\n        </div>\n        <br/>\n        <b><span style=\"font-family: 黑体; font-size: 28pt;\">江西省房屋建筑和市政基础设施工程施工</span></b>\n        <br/>\n        <br/>\n        <b><span style=\"font-family: 黑体; font-size: 30pt;\">招标公告</span></b><br/><br/>\n        <div style=\"text-align: center;font-size: 16pt;\">\n           \n                               项目编号：3609251906130201        招标编号： YCJNZB2019024 \n            <br/>\n            <br/>\n        </div>\n        <p style=\"text-align: center\">\n            </p><table border=\"1\" cellpadding=\"0\" cellspacing=\"0\" width=\"90%\">\n                <tbody><tr height=\"30\">\n                    <td align=\"center\" colspan=\"6\">\n                        <b><span style=\"font-family: 黑体; font-size: 18pt\">招标条件及工程基本情况</span></b>\n                    </td>\n                </tr>\n                <tr>\n                    <td style=\"width:16%;\">\n                        <span style=\"font-family: 黑体; font-size: 16pt\">招标单位名称</span>\n                    </td>\n                    <td colspan=\"5\" style=\"width:80%;\">\n                        <span lang=\"EN-US\" style=\"font-family: 黑体; font-size: 16pt\">靖安县高湖镇人民政府</span>\n                    </td>\n                </tr>\n                <tr>\n                    <td style=\"width:16%;\">\n                        <span style=\"font-famil