INSERT INTO `t_project` VALUES (117, '[吉安市本级][非网招]吉安市高铁新区核心区“五指峰”建筑群组团中科创中心和总部经济大楼  EPC项目招标公告', '房建市政', '', '<div class=\"article-info\">\n            				<h1>[吉安市本级]<font color=\"#0066FF\">[非网招]</font>吉安市高铁新区核心区“五指峰”建筑群组团中科创中心和总部经济大楼  EPC项目招标公告</h1>\n            				<p class=\"infotime\">\n            					[2019-06-15]\n            				</p>\n            				<div class=\"con\" style=\"margin-top: 31px;\"><p>\n</p>\n<p align=\"center\" class=\"MsoNormal\" style=\"margin-right: 0pt; margin-left: 0pt; text-align: center; line-height: 150%; background: rgb(255, 255, 255);\"><br/>\n</p>\n<p class=\"MsoNormal\" style=\"text-indent:21.0000pt;mso-char-indent-count:2.0000;text-autospace:ideograph-numeric;\nmso-pagination:none;line-height:150%;background:rgb(255,255,255);\"><span font-size:10.5000pt;mso-font-kerning:0.0000pt;\"=\"\" style=\"mso-spacerun:\" yes\";font-family:宋体;line-height:150%;=\"\"><font face=\"宋体\">本招标项目</font></span><u><span mso-font-kerning:0.0000pt;\"=\"\" style=\"mso-spacerun:\" text-decoration:underline;text-underline:single;font-size:10.5000pt;=\"\" yes\";font-family:宋体;line-height:150%;=\"\"><font face=\"宋体\">吉安市高铁新区核心区</font>“五指峰”建筑群组团中科创中心和总部经济大楼EPC项目</span></u><span font-size:10.5000pt;mso-font-kerning:0.0000pt;\"=\"\" style=\"mso-spacerun:\" yes\";font-family:宋体;line-height:150%;=\"\"><font face=\"宋体\">项目已批准建设，项目业主为</font></span><u><span mso-font-kerning:0.0000pt;\"=\"\" style=\"mso-spacerun:\" text-decoration:underline;text-underline:single;font-size:10.5000pt;=\"\" yes\";font-family:宋体;line-height:150%;=\"\"><font face=\"宋体\">江西省吉安市建筑安装工程总公司</font></span></u><span font-size:10.5000pt;mso-font-kerning:0.0000pt;\"=\"\" style=\"mso-spacerun:\" yes\";font-family:宋体;line-height:150%;=\"\"><font face=\"宋体\">，建设资金来自</font></span><u><span mso-font-kerning:0.0000pt;\"=\"\" style=\"mso-spacerun:\" text-decoration:underline;text-underline:single;font-size:10.5000pt;=\"\" yes\";font-family:宋体;line-height:150%;=\"\"> <font face=\"宋体\">自筹</font> </span></u><