mapper.sql:

			and t_project_announce.id = #{query.projectAnnounceId}


			and t_project_announce.id  >=  #{query.projectAnnounceIdMin}


			and t_project_announce.id  <=  #{query.projectAnnounceIdMax}


			and t_project_announce.id  >=  #{query.projectAnnounceIdMin} and t_project_announce.id  <=  #{query.projectAnnounceIdMax}


			and t_project_announce.project_id = #{query.projectAnnounceProjectId}


			and t_project_announce.project_id  >=  #{query.projectAnnounceProjectIdMin}


			and t_project_announce.project_id  <=  #{query.projectAnnounceProjectIdMax}


			and t_project_announce.project_id  >=  #{query.projectAnnounceProjectIdMin} and t_project_announce.project_id  <=  #{query.projectAnnounceProjectIdMax}


			and t_project_announce.announce_desc like concat('%', #{query.projectAnnounceAnnounceDesc}, '%')


			and t_project_announce.first_candidate like concat('%', #{query.projectAnnounceFirstCandidate}, '%')


			and t_project_announce.second_candidate like concat('%', #{query.projectAnnounceSecondCandidate}, '%')


			and t_project_announce.third_candidate like concat('%', #{query.projectAnnounceThirdCandidate}, '%')


			and t_project_announce.source_url like concat('%', #{query.projectAnnounceSourceUrl}, '%')


			and t_project_announce.inword_html_url like concat('%', #{query.projectAnnounceInwordHtmlUrl}, '%')


			and t_project_announce.version = #{query.projectAnnounceVersion}


			and t_project_announce.version  >=  #{query.projectAnnounceVersionMin}


			and t_project_announce.version  <=  #{query.projectAnnounceVersionMax}


			and t_project_announce.version  >=  #{query.projectAnnounceVersionMin} and t_project_announce.version  <=  #{query.projectAnnounceVersionMax}


			and t_project_announce.create_time = #{query.projectAnnounceCreateTime}


			and t_project_announce.create_time  >=  #{query.projectAnnounceCreateTimeMin}


			and t_project_announce.create_time  <=  #{query.projectAnnounceCreateTimeMax}


			and t_project_announce.create_time  >=  #{query.projectAnnounceCreateTimeMin} and t_project_announce.create_time  <=  #{query.projectAnnounceCreateTimeMax}


			and t_project_announce.update_time = #{query.projectAnnounceUpdateTime}


			and t_project_announce.update_time  >=  #{query.projectAnnounceUpdateTimeMin}


			and t_project_announce.update_time  <=  #{query.projectAnnounceUpdateTimeMax}


			and t_project_announce.update_time  >=  #{query.projectAnnounceUpdateTimeMin} and t_project_announce.update_time  <=  #{query.projectAnnounceUpdateTimeMax}


			and t_project_announce.is_active = #{query.projectAnnounceIsActive}


			and t_project_announce.is_active  >=  #{query.projectAnnounceIsActiveMin}


			and t_project_announce.is_active  <=  #{query.projectAnnounceIsActiveMax}


			and t_project_announce.is_active  >=  #{query.projectAnnounceIsActiveMin} and t_project_announce.is_active  <=  #{query.projectAnnounceIsActiveMax}


			and t_project.title like concat('%', #{query.projectTitle}, '%')


        and
        t_project_announce.project_id = t_project.id

mapper.select:
select

from t_project_announce, t_project



order by

    ${query.sortColumn} ${query.sortOrder}


    t_project_announce.update_time desc, t_project_announce.create_time desc

limit #{query.beginIndex}, #{query.pageSize}
